var trefoil = 'data:image/jpeg;base64,';
var scissors = 'data:image/jpeg;base64,';
var tick = 'data:image/jpeg;base64,';

function printADM27(guide: Guide, guardian: Guardian, activity: Activity) {
    var doc = new TsPdf();//'portrait', 'mm', 'a4');
    printPageOne(doc, guide, guardian, activity);
    doc.addPage();
    printPageTwo(doc, guide, guardian, activity);
    //doc.output('datauri');
    doc.output('dataurlnewwindow');
}

function fmtDate(value: Date) {
    if (value) {
        var ms:string[] = ['Jan', 'Feb', 'Mar', 'Apr', 'Jun', 'Jul', 'Aug', 'Sep', 'Oct', 'Nov', 'Dec'];
        var dt = new Date(value.toString());
        var cdate = dt.getDate();
        var cmonth = ms[dt.getMonth() - 1];
        var cyear = dt.getFullYear();
        return cdate.toString() + '-' + cmonth + '-' + cyear.toString();
    } else {
        return '';
    }
}

function drawTick(doc, checked: boolean, x_true, y_true, x_false, y_false, width, height) {
    if (checked) {
        doc.addImage(tick, 'JPEG', x_true, y_true, width, height);
    } else {
        doc.addImage(tick, 'JPEG', x_false, y_false, width, height);
    }
}

function drawCheck(doc, checked: boolean, x: number, y: number, width: number, height: number) {
    if (checked) {
        doc.addImage(tick, 'JPEG', x + 0.5, y + 0.4, width, height);
    }
}

